import { useRef,useState } from 'react'
import classes from './MealItemForm.module.css'
import MealItemInput from './MealItemInput'
import React from 'react'

function MealItemForm(props){
    const [amountstate,update]=useState(true)
    const inputRef=useRef()

    function formHandler(event){
        event.preventDefault();
        let enteredAmount=amountInputref.current.value;
        let enteredAmountNumber =+enteredAmount
        if(enteredAmount.trim().length ===0 || enteredAmountNumber <1 || enteredAmountNumber >5){
                update(false);
                return;
        }   
        props.OnAddToCart(enteredAmountNumber)
    }

    return(
        <form className={classes.form} onSubmit={formHandler}>
            <MealItemInput label='Amount'
            ref={inputRef}
            input={{
                id:'amount_'+props.itemid,
                type:'number',
                defaultValue:1,
                step:1,
                min:1,
                max:5
            }}/>
            <button > +ADD</button>
            {!amountstate && <p>please enter correct item details</p>}
        </form>
    )
}
export default MealItemForm
