import CartContext from "./cartContext"
import { useReducer } from "react"

const defaultvalue={
    items:[],
    totalamount:0,
}

function cartreducer(state,action){
    if(action.type === 'ADD'){
        const updateditems=state.items.concat(action.item);
        const updatedamount =state.totalamount + action.items.amount * action.items.price
        return{
            items:updateditems,
            totalamount:updatedamount
        }
    }
    return defaultvalue
}
function ContextProvider(props){
    const[state,cartdispatch]=useReducer(cartreducer,defaultvalue)

    function AddToCartHandler(item){
        cartdispatch({type:'ADD',item:item})
    }

    function removetocartHandler(id){
        return
    }
    const CtxItems={
        items:state.items,
        totalamount:state.totalamount,
        AddItem:AddToCartHandler,
        removetocart:removetocartHandler
    }
    return(
        <CartContext.Provider value={CtxItems}>
            {props.children}
        </CartContext.Provider>
    )
}

export default ContextProvider
